<?phpnamespace AtlantisGroup\OrmAG\Service;use AtlantisGroup\OrmAG\DataAccess\DataAccessInterface;use AtlantisGroup\OrmAG\DataMapper\DataMapperInterface;use Psr\Log\LoggerInterface;use Symfony\Component\Stopwatch\Stopwatch;use Symfony\Contracts\Translation\TranslatorInterface;/** * Class AbstractService * @package AtlantisGroup\OrmAG\Service */abstract class AbstractService implements ServiceInterface{    /**     * @var DataAccessInterface     */    protected DataAccessInterface $dataAccess;    /**     * @var DataMapperInterface     */    protected DataMapperInterface $dataMapper;    /**     * @var TranslatorInterface     */    protected TranslatorInterface $translator;    /**     * @var Stopwatch     */    protected Stopwatch $stopwatch;    /**     * @var bool     */    protected bool $debug;    /**     * @var LoggerInterface     */    protected LoggerInterface $logger;    /**     * @param DataAccessInterface $dataAccess     * @param DataMapperInterface $dataMapper     * @param TranslatorInterface $translator     * @param LoggerInterface $logger     * @param bool $debug     */    public function __construct(DataAccessInterface $dataAccess, DataMapperInterface $dataMapper, TranslatorInterface $translator, LoggerInterface $logger = null, bool $debug = false)    {        $this->dataAccess = $dataAccess;        $this->dataMapper = $dataMapper;        $this->translator = $translator;        $this->logger = $logger;        $this->debug = $debug;        $this->dataAccess->setDebug($this->debug);    }    /**     * @param Stopwatch $stopwatch     * @return AbstractService     */    public function setStopwatch(Stopwatch $stopwatch): ServiceInterface    {        $this->stopwatch = $stopwatch;        return $this;    }    /**     * @param bool $debug     * @return ServiceInterface     */    public function setDebug(bool $debug): ServiceInterface    {        $this->debug = $debug;        $this->dataAccess->setDebug($debug);        return $this;    }}