<?phpnamespace AtlantisGroup\OrmAG\Wrapper;use AtlantisGroup\OrmAG\Entity\EntityInterface;use AtlantisGroup\OrmAG\Object\FakeObjectInterface;trait WrapperTrait{    /**     * @var EntityInterface     */    protected EntityInterface $wrapped;    public function __construct(EntityInterface &$wrapped)    {        $this->wrapped = $wrapped;    }    /**     * @param bool $checkSub     * @return EntityInterface     */    public function getRealObject(bool $checkSub = true): EntityInterface    {        if ($checkSub === true && $this->wrapped instanceof FakeObjectInterface) {            return $this->wrapped->getRealObject();        } else {            return $this->wrapped;        }    }}