<?php$autoloader = function () {    $files = [        __DIR__ . '/../vendor/autoload.php', // stand-alone package        __DIR__ . '/../../../autoload.php', // composer dependency    ];    foreach ($files as $file) {        if (is_file($file)) {            define('VENDOR_DIR', dirname($file));            require_once $file;            return true;        }    }    return false;};require_once  __DIR__ . '/fixtures/classes.php';if (!$autoloader()) {    die(        'You need to set up the project dependencies using the following commands:' . PHP_EOL .        'curl -s http://getcomposer.org/installer | php' . PHP_EOL .        'php composer.phar install' . PHP_EOL    );}